import React, { useEffect, useState } from 'react';
import ClientHeader from '../common/ClientHeader';
import { Link, useNavigate } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import AnimalsList from './AnimalsList';
import NotFound from '../common/NotFound';

const ClientPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Функция для проверки существования пользователя по его ID
    const checkUserExistence = async () => {
      try {
        const response = await fetch(`http://localhost:3000/client/${userId}`);
        if (response.ok) {
          const user = await response.json();
          setUserData(user);
        } else if (response.status === 404) {
          navigate('/notfound');
        }
      } catch (error) {
        console.error('Ошибка при запросе к серверу:', error);
      } finally {
        setLoading(false); 
      }
    };

    checkUserExistence();
  }, [userId,navigate]); // Запускаем проверку при изменении userId

  if (loading) {
    return <div>Loading...</div>; // Можно также использовать спиннер загрузки
  }

  return (
    <div>
      {userData ? (
        <div>
          <ClientHeader />
          <div className="content">
            <h2>Страница клиента {userId}</h2>
            <div className="form">
              <AnimalsList />
              <Link to={`/client/${userId}/animalform`}>
                <button className='linkButton'>Добавить животное</button>
              </Link>
              <Link to={`/client/${userId}/requestform`}>
                <button className='linkButton'>Добавить заявку</button>
              </Link>
            </div>
          </div>
        </div>
      ) : (
        <NotFound />
      )}
    </div>
  );
};

export default ClientPage;
