import React, { useEffect, useState } from 'react';
import ClientHeader from '../common/ClientHeader';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const ClientPage = () => {
  const location = useLocation();
  const userId = location.state.userId;
  const [animals,setAnimals] = useState([]);

  useEffect(()=>{
    const fetchAnimals = async () => {
      try{
        const responce = await axios.get('http://localhost:3000/login');
        setAnimals(responce.data);
      } catch(error){
        console.error('Произошла ошибка при получении списка питомцев', error);
      }
    }
    fetchAnimals();
  }, [userId]);

  return (
    <div>
      <ClientHeader />
      <div className="content">
        <h2>Страница клиента {userId}</h2>
        <div className="form">
          {/* Список питомцев */}
          <ul>
            {animals.map((animal) => (
              <li key={animal.id}>{animal.name}</li>
            ))}
          </ul>
          <Link to='/client/animalform'>
            <button>Добавить животное</button>
          </Link>

          {/* Форма для создания заявки на выгул */}
          <Link to='/client/requestform'>
            <button>Добавить заявку</button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default ClientPage;
