import React, { useEffect, useState } from "react";

function AnimalsList(userId) {
    const [animals, setAnimals] = useState([]);

    useEffect(() => {
        const fetchAnimals = async () => {
            try {
                const response = await fetch(`http://localhost:3000/api/users/${userId}/animals`);
                if (response.ok) {
                    const animalsData = await response.json();
                    setAnimals(animalsData);
                } else {
                    console.error('Ошибка при получении списка животных');
                }
            } catch (error) {
                console.error('Ошибка при выполнении запроса к серверу:', error);
            }
        };

        fetchAnimals();
    }, [userId]);

    return(
        <div>
            <h3>Список животных</h3>
            <ul>
                {animals.map((animal) => {
                    <li key={animal.id}>{animal.name}</li>
                })}
            </ul>
        </div>
    );
}

export default AnimalsList;
